import React, { useEffect, useState } from 'react';
import './HomeSections.css'

const teamMembers = [
    {
        name: 'Matthew Friters',
        role: 'Founder & CEO',
        background: 'Johns Hopkins University (MBA) and United States Military Academy (BS Econ), US Army (17 Years), US Space Force (3 Years)',
        bio: `Matt came up with the concept of Give&Game, as a father that went through multiple moves during his career. During his time at Johns Hopkins MBA, he created Give&amp;Game. He’s passionate about helping his community and getting kids involved in activities, sports and STEM programs.`,
        image: '/Assets/IMG_5566.jpeg'
    }
]



function HomeSections() {
    const [fadeIn, setFadeIn] = useState(false);

    useEffect(() => {
      const timeout = setTimeout(() => setFadeIn(true), 300);
      return () => clearTimeout(timeout);
    }, []);

    return (
      <div className="home-sections">
        <section className={`mission ${fadeIn ? 'fade-in' : ''}`}>
          <h2>Our Mission</h2>
          <p>
            Give & Game is a brand new platform designed with one clear goal in mind: making a difference in the
            community by giving kids and families equal opportunities to participate in Youth Sports and Activities.
          </p>
        </section>

        <div className="divider"></div>

        <section className="team">
          <h2>Meet the Team</h2>
          {teamMembers.map((member, index) => (
            <div className={`team-member ${index % 2 === 0 ? 'left' : 'right'}`} key={index}>
              <img src={member.image} alt={member.name} />
              <div className="bio">
                <h3>{member.name}</h3>
                <h4>{member.role}</h4>
                <p>{member.bio}</p>
              </div>
            </div>
          ))}
        </section>
      </div>
    );
  }

  export default HomeSections;
