import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

function RolePage () {
    const [role, setRole] = useState('');
    const navigate = useNavigate();

    const handleSubmit = () => {

        console.log('Selected Role:', role);
        navigate('');
    }



    return (
        <div className='role-selection'>
            <h2>I want to Give and Game As:</h2>

            <label>
                <input 
                    type="radio"
                    name="role"
                    value="Coach/Team"
                    onChange={(e) => setRole(e.target.value)}
                />
                Coach/Team Profile
            </label>
            <label>
                <input
                    type="radio"
                    name="role"
                    value="Parent/Guardian"
                    onChange={(e) => setRole(e.target.value)}
                />
                Parent/Guardian
            </label>
            <label>
                <input 
                    type="radio"
                    name="role"
                    value="Sponsor/Corporate Sponsor"
                    onChange={(e) => setRole(e.target.value)}
                />
            </label>

            <button onClick={handleSubmit} disabled={!role}>Next</button>
        </div>

    )
}

export default RolePage
